const countdown=document.querySelector(".countdown"),d_day=new Date("September 5,2020 10:00:00").getTime(),updater=setInterval(()=>{const now=(new Date).getTime(),distance=d_day-now,days=Math.floor(distance/864e5),hours=Math.floor(distance%864e5/36e5),mins=Math.floor(distance%36e5/6e4),seconds=Math.floor(distance%6e4/1e3);countdown.innerHTML=`\n<div><h1>${days}</h1><span>Days</span></div>\n<div><h1>${hours}</h1><span>Hours</span></div>\n<div><h1>${mins}</h1><span>Minutes</span></div>\n<div><h1>${seconds}</h1><span>Seconds</span></div>\n`},1e3);